mapper.update:
UPDATE example
SET example_title = #{exampleTitle}, answer = #{answer}
WHERE question_no = #{questionNo} AND example_no = #{exampleNo}

mapper.insert:
INSERT INTO example(
	question_no
	, example_idx
	, example_title
	, answer
) VALUES (
	#{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{answer}
)
