mapper.update:
UPDATE question 
SET question_title = #{questionTitle}
WHERE test_no = #{testNo}  AND question_no = #{questionNo}

mapper.insert:
INSERT INTO question(
	test_no
	, question_idx
	, question_title
	, question_score
) VALUES (
	#{testNo}
	, #{questionIdx}
	, #{questionTitle}
	, #{questionScore}
)
