mapper.update:
UPDATE test 
SET test_title = #{testTitle}, createdate = #{createdate}
WHERE test_no = #{testNo}

mapper.insert:
INSERT INTO test(
	test_title
	, question_count
	, createdate
) VALUES (
	#{testTitle}
	, #{questionCount}
	, #{createdate}
)

mapper.select:
SELECT test_no testNo , test_title testTitle , createdate FROM test
test_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY createdate DESC LIMIT #{beginRow}, #{rowPerPage}
