mapper.update:
UPDATE test 
SET test_title = #{testTitle}, createdate = #{createdate}
WHERE test_no = #{testNo}

mapper.insert:
INSERT INTO test(
	test_title
	, question_count
	, createdate
) VALUES (
	#{testTitle}
	, #{questionCount}
	, #{createdate}
)

mapper.select:
SELECT t.test_no testNo , t.test_title testTitle , t.createdate FROM test t INNER JOIN question q ON t.test_no = q.test_no INNER JOIN example e ON q.question_no = e.question_no
test_title LIKE CONCAT('%', #{searchWord}, '%')
GROUP BY t.createdate DESC LIMIT #{beginRow}, #{rowPerPage}
